import { faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { FastField, Form, Formik } from 'formik';
import React, { useState } from 'react';
import { useParams } from 'react-router';
import {
  Button,
  Col,
  Container,
  FormFeedback,
  FormGroup,
  Input,
  Label,
  Modal,
  ModalBody,
  ModalHeader,
  Row,
  Table,
} from 'reactstrap';
import { ORDER } from '../../assets/Json/ORDER';

import * as yup from 'yup';
import './OrderDetails.css';
let validationSchema = yup.object().shape({
  name: yup.string().required('Required').min(3),
  city: yup.string('Enter a Valid City').required('Required'),
  phone: yup.string().phone('IN').required('Required'),
  state: yup.string().required('Required'),
  email: yup.string().email('Enter a valid email').required('Required'),
  password: yup
    .string()
    .required('required')
    .min(8, 'Minimum 8 characters required'),
});
const initialValues = {
  name: '',
  email: '',
  city: '',
  state: '',
  password: '',
  phone: '',
};
const OrderDetails = () => {
  const [createModel, setcreateModel] = useState(false);
  let param = useParams();
  const i = parseInt(param.id);
  const data = ORDER[i];

  const toggle = () => setcreateModel(!createModel);
  const closeIcon = (
    <FontAwesomeIcon
      onClick={toggle}
      className='model_close_btn'
      icon={faTimes}
    />
  );
  return (
    <Container>
      <h4 className='pt-4 pb-4'>Order Details</h4>
      <div className='cardDetials'>
        <h4 className='text-light p-3 distributorHeading'>
          {data.distributor}
        </h4>
        <Row className='px-3'>
          <Col
            xs={3}
            sm={3}
            md={3}
            lg={2}
            xl={2}
            className='pb-1 text-light column1'>
            Order ID
          </Col>
          <Col
            xs={9}
            sm={9}
            md={9}
            lg={6}
            xl={6}
            className='pb-1 text-light column2'>
            : {data.id}
          </Col>
          <Col
            xs={3}
            sm={3}
            md={3}
            lg={2}
            xl={2}
            className='pb-1 text-light column1'>
            Total Item
          </Col>
          <Col
            xs={9}
            sm={9}
            md={9}
            lg={2}
            xl={2}
            className='pb-1 text-light column2'>
            : {data.totalItem}
          </Col>
        </Row>
        <Row className='px-3'>
          <Col
            xs={3}
            sm={3}
            md={3}
            lg={2}
            xl={2}
            className='pb-1 text-light column1'>
            Order Date
          </Col>
          <Col
            xs={9}
            sm={9}
            md={9}
            lg={6}
            xl={6}
            className='pb-1 text-light column2-'>
            : {data.date}
          </Col>
          <Col
            xs={3}
            sm={3}
            md={3}
            lg={2}
            xl={2}
            className='pb-1 text-light column1'>
            Amount
          </Col>
          <Col
            xs={9}
            sm={9}
            md={9}
            lg={2}
            xl={2}
            className='pb-1 text-light column2'>
            : {data.amount}
          </Col>
        </Row>
        <Row className='px-3 pb-4'>
          <Col
            xs={3}
            sm={3}
            md={3}
            lg={2}
            xl={2}
            className='pb-1 text-light column1'>
            Address
          </Col>
          <Col
            xs={9}
            sm={9}
            md={9}
            lg={6}
            xl={6}
            className='pb-1 text-light column2-'>
            : {data.address}
          </Col>
          <Col
            xs={3}
            sm={3}
            md={3}
            lg={2}
            xl={2}
            className='pb-1 text-light column1'>
            Status
          </Col>
          <Col
            xs={9}
            sm={9}
            md={9}
            lg={2}
            xl={2}
            className='pb-1 text-light column2'>
            : {data.status}
          </Col>
        </Row>
      </div>
      <div className='tableDiv mt-5 mb-3'>
        <Table>
          <thead>
            <tr>
              <th>Item</th>
              <th>Size</th>
              <th>Quantity</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {data.orders.map((data, i) => {
              return (
                <tr key={i}>
                  <td>{data.item}</td>
                  <td>{data.size}</td>
                  <td>{data.quantity}</td>
                  <td>{data.price}</td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </div>
      <div className='d-flex w-100 justify-content-end'>
        <Button color='danger' className='mr-3 mb-3'>
          Cancel
        </Button>
        <Button className='mb-3'>Order Dispatch</Button>
      </div>
      <Modal isOpen={createModel} toggle={toggle}>
        <ModalHeader toggle={toggle} close={closeIcon}></ModalHeader>
        <ModalBody>
          <div className='d-flex justify-content-center w-100 mb-2'>
            <h4>Dispatch Order</h4>
          </div>
          <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={onSubmit}
            isInitialValid={() => validationSchema.isValidSync(initialValues)}>
            {({
              values,
              touched,
              errors,
              isValid,
              handleChange,
              handleBlur,
              setFieldValue,
              dirty,
            }) => (
              <>
                <Form className='form'>
                  <FormGroup>
                    <Label for='Distributor'>Receipt Number</Label>
                    <FastField
                      as={Input}
                      name='name'
                      value={values.name}
                      onChange={handleChange}
                      invalid={touched.name && errors.name}
                      valid={dirty && !errors.name}
                    />
                    <FormFeedback>Enter a valid name</FormFeedback>
                  </FormGroup>
                  <FormGroup>
                    <Label for='State'>State</Label>
                    <FastField
                      as={Input}
                      name='state'
                      value={values.state}
                      onChange={handleChange}
                      invalid={touched.state && errors.state}
                      valid={dirty && !errors.state}
                    />
                    <FormFeedback>Enter a valid state</FormFeedback>
                  </FormGroup>
                  <FormGroup>
                    <Label for='City'>City</Label>
                    <FastField
                      as={Input}
                      name='city'
                      value={values.city}
                      onChange={handleChange}
                      invalid={touched.city && errors.city}
                      valid={dirty && !errors.city}
                    />
                    <FormFeedback>Enter a valid city</FormFeedback>
                  </FormGroup>
                  <FormGroup>
                    <Label for='Contact Number'>Contact Number</Label>
                    <FastField
                      as={Input}
                      name='phone'
                      value={values.phone}
                      onChange={handleChange}
                      invalid={touched.phone && errors.phone}
                      valid={dirty && !errors.phone}
                    />
                    <FormFeedback>Enter a valid phone number</FormFeedback>
                  </FormGroup>
                  <FormGroup>
                    <Label for='Email Id'>Email Id</Label>
                    <FastField
                      as={Input}
                      name='email'
                      type='email'
                      value={values.email}
                      onChange={handleChange}
                      invalid={touched.email && errors.email}
                      valid={dirty && !errors.email}
                    />
                    <FormFeedback>Enter a Email Id</FormFeedback>
                  </FormGroup>
                  <FormGroup>
                    <Label for='Password'>Password</Label>
                    <FastField
                      as={Input}
                      bsSize='sm'
                      type='password'
                      name='password'
                      value={values.password}
                      onChange={handleChange}
                      invalid={touched.password && errors.password}
                      valid={dirty && !errors.password}
                    />
                    <FormFeedback>Enter a valid 10 digit password</FormFeedback>
                  </FormGroup>
                  <Button
                    type='submit'
                    className='w-100 mt-3'
                    color='secondary'
                    disabled={!dirty || !isValid}>
                    Save
                  </Button>
                </Form>
              </>
            )}
          </Formik>
        </ModalBody>
      </Modal>
    </Container>
  );
};

export default OrderDetails;
