import axios from 'axios';
import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { useFilters, usePagination, useSortBy, useTable } from 'react-table';
import { Button, Container } from 'reactstrap';
import TableTax from '../../../components/Table/TableTax';

const Item = () => {
  const [item, setItem] = useState([
    {
      id: '',
      sku: '',
      hierarchy: '',
      category: '',
      type: '',
      hmlcategory: '',
      mrp: '',
    },
  ]);
  const Loaditem = useCallback(async () => {
    await axios
      .get('http://localhost:3003/item')
      .then((response) => {
        setItem(response.data);
        console.log('response.data', response.data);
      })
      .catch((error) => {
        console.log('catch', error);
      });
  }, [setItem]);
  const editUser = useCallback(
    async (e) => {
      e.preventDefault();
      console.log('editUser', e);
      // await axios
      //   .put(`http://localhost:3003/item/${rowIdx}`, {})
      //   .then((response) => {
      //     console.log('response', response);
      //   })
      //   .catch((error) => {
      //     console.log('error', error);
      //   });
      Loaditem();
    },
    [Loaditem]
  );
  useEffect(() => {
    Loaditem();
  }, [Loaditem]);
  const ITEMS_COLUMN = useMemo(
    () => [
      {
        Header: 'id',
        accessor: 'id',
      },
      {
        Header: 'SKU',
        accessor: 'sku',
      },
      { Header: 'Product Hierarchy', accessor: 'hierarchy' },
      { Header: 'Product Category', accessor: 'category' },
      { Header: 'Product Type', accessor: 'type' },
      { Header: 'HML Category', accessor: 'hmlcategory' },
      { Header: 'MRP', accessor: 'mrp' },

      {
        Header: 'Actions',
        accessor: 'action',
        disableSortBy: true,
        Cell: (props) => {
          // const rowIdx = props.row.original.id;
          return (
            <div className='d-flex justify-content-around'>
              <Button onClick={(e) => editUser(e)}>Edit</Button>
            </div>
          );
        },
      },
    ],
    [editUser]
  );
  const columns = useMemo(() => ITEMS_COLUMN, [ITEMS_COLUMN]);
  const data = useMemo(() => item, [item]);
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    page,
    nextPage,
    previousPage,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state,
    gotoPage,
    pageCount,
    setPageSize,
    prepareRow,
    setFilter,
  } = useTable(
    {
      columns,
      data,
    },
    useFilters,
    useSortBy,
    usePagination
  );

  const { pageIndex, pageSize } = state;
  return item === '' ? (
    <div>{console.log('item in empty', item)}</div>
  ) : (
    <>
      <Container>
        <TableTax
          columns={columns}
          data={data}
          getTableProps={getTableProps}
          getTableBodyProps={getTableBodyProps}
          headerGroups={headerGroups}
          page={page}
          nextPage={nextPage}
          previousPage={previousPage}
          canNextPage={canNextPage}
          canPreviousPage={canPreviousPage}
          pageOptions={pageOptions}
          state={state}
          gotoPage={gotoPage}
          pageCount={pageCount}
          setPageSize={setPageSize}
          prepareRow={prepareRow}
          setFilter={setFilter}
          pageIndex={pageIndex}
          pageSize={pageSize}
        />
      </Container>
    </>
  );
};

export default Item;
