import { faPen, faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { FastField, Formik } from 'formik';
import React, { useState } from 'react';
import {
  Button,
  Form,
  FormFeedback,
  FormGroup,
  Input,
  Label,
  Modal,
  ModalBody,
  ModalHeader,
} from 'reactstrap';
import * as yup from 'yup';
import './Hml.css';
const initialValues = {
  High: '',
  Low: '',
  Mid: '',
};
let validationSchema = yup.object().shape({
  High: yup.number(),
  Mid: yup.number(),
  Low: yup.number(),
});
const Hml = ({ type, margin, hirarchy, selectedRegion }) => {
  const [createModel, setCreateModel] = useState(false);
  const toggle = () => {
    setCreateModel(!toggle);
  };
  const closeIcon = (
    <FontAwesomeIcon
      onClick={toggle}
      className='model_close_btn'
      icon={faTimes}
    />
  );
  return (
    <div>
      <div className='add_margin my-3 d-flex'>
        <div className='d-flex icon_container'>
          <FontAwesomeIcon
            onClick={() => setCreateModel(!createModel)}
            className='editicon'
            icon={faPen}
          />
        </div>
        <p className='p'>Type</p>
        <h3>{type}</h3>
        <div className='margin'>
          <h1>{margin}%</h1>
          <p className='p'>Margin</p>
        </div>
        <div>
          <p className='p'>Hirarchy</p>
          <h3>{hirarchy}</h3>
        </div>
      </div>
      <Modal isOpen={createModel} toggle={toggle}>
        <ModalHeader toggle={toggle} close={closeIcon}></ModalHeader>
        <ModalBody>
          <div className='d-flex justify-content-center w-100 mb-2'>
            <h4>Edit Margin</h4>
          </div>
          <div className='defaults '>
            <div className='d-flex justify-content-center'>
              <p className='region'>{'State: '} </p>
              <p className=''>{` ${selectedRegion}`}</p>
            </div>
            <div className='d-flex justify-content-center'>
              <p className='region'>{'State: '} </p>
              <p className=''>{` ${selectedRegion}`}</p>
            </div>
          </div>
          <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            // onSubmit={onSubmit}
            isInitialValid={() => validationSchema.isValidSync(initialValues)}>
            {({
              values,
              touched,
              errors,
              isValid,
              handleChange,
              handleBlur,
              setFieldValue,
              dirty,
            }) => (
              <>
                <Form className='form'>
                  <FormGroup>
                    <Label for='Distributor'>Receipt Number</Label>
                    <FastField
                      as={Input}
                      name='recipt'
                      value={values.Low}
                      onChange={handleChange}
                      invalid={touched.Low && errors.Low}
                      valid={dirty && !errors.Low}
                    />
                    <FormFeedback>Enter a valid recipt number</FormFeedback>
                  </FormGroup>
                  <Button
                    type='submit'
                    className='w-100 mt-3'
                    color='secondary'
                    disabled={!dirty || !isValid}>
                    Save
                  </Button>
                </Form>
              </>
            )}
          </Formik>
        </ModalBody>
      </Modal>
    </div>
  );
};

export default Hml;
