import { format, parseISO } from 'date-fns';
import React, { useMemo, useState } from 'react';
import ReactDatePicker from 'react-datepicker';
import { useFilters, usePagination, useSortBy, useTable } from 'react-table';
import { Container, FormGroup, Input, InputGroup } from 'reactstrap';
import { ORDER, ORDER_COLUMN } from '../../assets/Json/ORDER';
import TableTax from '../../components/Table/TableTax';
import '../../components/Table/TableTax.css';
const DistributorOrder = () => {
  const columns = useMemo(() => ORDER_COLUMN, []);
  const [startDate, setStartDate] = useState(new Date('2014/02/08'));
  const [endDate, setEndDate] = useState(new Date('2014/02/10'));
  const data = useMemo(() => ORDER, []);
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    page,
    nextPage,
    previousPage,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state,
    gotoPage,
    pageCount,
    setPageSize,
    prepareRow,
    setFilter,
  } = useTable(
    {
      columns,
      data,
    },
    useFilters,
    useSortBy,
    usePagination
  );
  const { pageIndex, pageSize } = state;
  const handleStateChange = (e, prop) => {
    const value = e.target.value || undefined;
    setFilter(prop, value); // Update the show.name filter. Now our table will filter and show only the rows which have a matching value
  };
  // Update the state when input changes

  const states = [...new Set(ORDER.map((x) => x.state))];
  const cities = [...new Set(ORDER.map((x) => x.city))];
  const startDat = (e) => {
    let dates = format(parseISO(e.target.value), 'dd-MM-yyyy');
    console.log(dates);
    setStartDate(dates);
  };
  return (
    <Container>
      <h4 className='pt-4 pb-5'>Ditributor's Order</h4>
      <div className='d-flex w-60 ml-3  mb-3 filters'>
        <div className='d-flex flex-column mr-3 w-100'>
          <span>State</span>
          <FormGroup size='sm'>
            <Input
              type='select'
              name='select'
              onChange={(e) => handleStateChange(e, 'state')}
              id='exampleSelect'>
              <option value=''>All</option>
              {states.map((data, index) => {
                return (
                  <option value={data} key={index}>
                    {data}
                  </option>
                );
              })}
            </Input>
          </FormGroup>
        </div>
        <div className='d-flex flex-column mr-3 w-100'>
          <span>City</span>
          <InputGroup size='sm'>
            <Input
              type='select'
              name='select'
              onChange={(e) => handleStateChange(e, 'city')}
              id='exampleSelect'>
              <option value=''>All</option>
              {cities.map((data, index) => {
                return (
                  <option value={data} key={index}>
                    {data}
                  </option>
                );
              })}
            </Input>
          </InputGroup>
        </div>
        <div className='d-flex flex-column mr-3 w-100'>
          <span>Date</span>

          <ReactDatePicker
            selected={startDate}
            onChange={(date) => setStartDate(date)}
            selectsStart
            startDate={startDate}
            endDate={endDate}
          />
          <ReactDatePicker
            selected={endDate}
            onChange={(date) => setEndDate(date)}
            selectsEnd
            startDate={startDate}
            endDate={endDate}
            minDate={startDate}
          />
          {/* <Input
              type='date'
              name='date'
              onChange={(e) => dateFormat(e)}
              placeholder='date placeholder'
              value={dats}
            /> */}
        </div>
      </div>

      <TableTax
        columns={columns}
        data={data}
        getTableProps={getTableProps}
        getTableBodyProps={getTableBodyProps}
        headerGroups={headerGroups}
        page={page}
        nextPage={nextPage}
        previousPage={previousPage}
        canNextPage={canNextPage}
        canPreviousPage={canPreviousPage}
        pageOptions={pageOptions}
        state={state}
        gotoPage={gotoPage}
        pageCount={pageCount}
        setPageSize={setPageSize}
        prepareRow={prepareRow}
        setFilter={setFilter}
        pageIndex={pageIndex}
        pageSize={pageSize}
      />
    </Container>
  );
};

export default DistributorOrder;
