import { faPen } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useState } from 'react';
import './Hml.css';
const Hml = ({ type, margin, hirarchy }) => {
  const [createModel, setCreateModel] = useState(false);
  const toggle = () => {
    setCreateModel(!toggle);
  };

  return (
    <div>
      <div className='add_margin my-3 d-flex'>
        <div className='d-flex icon_container'>
          <FontAwesomeIcon
            onClick={() => toggle()}
            className='editicon'
            icon={faPen}
          />
        </div>
        <p className='p'>Type</p>
        <h3>{type}</h3>
        <div className='margin'>
          <h1>{margin}%</h1>
          <p className='p'>Margin</p>
        </div>
        <div>
          <p className='p'>Hirarchy</p>
          <h3>{hirarchy}</h3>
        </div>
      </div>
      <Modal isOpen={createModel} toggle={toggle}>
        <ModalHeader toggle={toggle} close={closeIcon}></ModalHeader>
        <ModalBody>
          <div className='d-flex justify-content-center w-100 mb-2'>
            <h4>Dispatch Order</h4>
          </div>
          <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={onSubmit}
            isInitialValid={() => validationSchema.isValidSync(initialValues)}>
            {({
              values,
              touched,
              errors,
              isValid,
              handleChange,
              handleBlur,
              setFieldValue,
              dirty,
            }) => (
              <>
                <Form className='form'>
                  <FormGroup>
                    <Label for='Distributor'>Receipt Number</Label>
                    <FastField
                      as={Input}
                      name='recipt'
                      value={values.recipt}
                      onChange={handleChange}
                      invalid={touched.recipt && errors.recipt}
                      valid={dirty && !errors.recipt}
                    />
                    <FormFeedback>Enter a valid recipt number</FormFeedback>
                  </FormGroup>
                  <FormGroup>
                    <Label for='Transporter Name'>Transporter Name</Label>
                    <FastField
                      as={Input}
                      name='transporter'
                      value={values.transporter}
                      onChange={handleChange}
                      invalid={touched.transporter && errors.transporter}
                      valid={dirty && !errors.transporter}
                    />
                    <FormFeedback>Enter a valid transporter name</FormFeedback>
                  </FormGroup>
                  <FormGroup>
                    <Label for='date'>Date</Label>
                    <FastField
                      as={Input}
                      type='date'
                      name='date'
                      value={values.date}
                      onChange={handleChange}
                      invalid={touched.date && errors.date}
                      valid={dirty && !errors.date}
                    />
                    <FormFeedback>Enter a valid date</FormFeedback>
                  </FormGroup>
                  <Button
                    type='submit'
                    className='w-100 mt-3'
                    color='secondary'
                    disabled={!dirty || !isValid}>
                    Save
                  </Button>
                </Form>
              </>
            )}
          </Formik>
        </ModalBody>
      </Modal>
    </div>
  );
};

export default Hml;
