import React from 'react';
import { useParams } from 'react-router';
import { Col, Container, Row } from 'reactstrap';
import { ORDER } from '../../assets/Json/ORDER';
import './OrderDetails.css';
const OrderDetails = (props) => {
  let param = useParams();
  const i = parseInt(param.id);
  const data = ORDER[i];
  console.log('data', data);
  console.log('orders');

  return (
    <Container>
      <h4 className='pt-4 pb-5'>Order Details</h4>
      <div className='cardDetials'>
        <h4 className='text-light my-3'>{data.distributor}</h4>
        <Row>
          <Col className='text-light'>Order ID : {data.id}</Col>
          <Col className='text-light'>Total Item : {data.totalItem}</Col>
        </Row>
        <Row>
          <Col className='text-light'>Order Date : {data.date}</Col>
          <Col className='text-light'>Amount : {data.amount}</Col>
        </Row>
        <Row>
          <Col className='text-light'>Address : {data.address}</Col>
          <Col className='text-light'>Status : {data.status}</Col>
        </Row>
      </div>
    </Container>
  );
};

export default OrderDetails;
