import React, { useState } from 'react';
import { useTable, useFilters, useSortBy, usePagination } from 'react-table';
import {
  Container,
  Table,
  Input,
  Button,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
} from 'reactstrap';
import { STATES } from '../../assets/Json/data';
import './TableC.css';
import {
  faLongArrowAltDown,
  faLongArrowAltUp,
  faSearch,
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
export const TableC = ({ columns, data, heading }) => {
  const {
    getTableProps, // table props from react-table
    getTableBodyProps, // table body props from react-table
    headerGroups, // headerGroups, if your table has groupings
    page, // rows for the table based on the data passed
    nextPage,
    previousPage,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state,
    gotoPage,
    pageCount,
    setPageSize,
    prepareRow, // Prepare the row (this function needs to be called for each row before getting the row props)
    setFilter, // The useFilter Hook provides a way to set the filter
  } = useTable(
    {
      columns,
      data,
    },
    useFilters,
    useSortBy,
    usePagination
  );

  const [filterInput, setFilterInput] = useState('');

  // Update the state when input changes
  const handleFilterChange = (e) => {
    const value = e.target.value || undefined;
    setFilter('name', value); // Update the show.name filter. Now our table will filter and show only the rows which have a matching value
    setFilterInput(value);
  };
  const handleSelectFilterChange = (e) => {
    const value = e.target.value || undefined;
    setFilter('state', value); // Update the show.name filter. Now our table will filter and show only the rows which have a matching value
  };
  const { pageIndex, pageSize } = state;
  return (
    <Container>
      <h4 className='pt-4 pb-5'>{heading}</h4>
      <div className='d-flex w-60 ml-3  mb-3 filters'>
        <div className='d-flex flex-column mr-3 w-100'>
          <span>Dealer</span>
          <InputGroup size='sm'>
            <Input
              value={filterInput}
              onChange={handleFilterChange}
              placeholder={'Search name'}
            />
            <InputGroupAddon addonType='append'>
              <InputGroupText>
                <FontAwesomeIcon className='icon' icon={faSearch} />
              </InputGroupText>
            </InputGroupAddon>
          </InputGroup>
        </div>
        <div className='d-flex flex-column mr-3 w-100'>
          <span>State</span>
          <InputGroup size='sm'>
            <Input
              type='select'
              name='select'
              onChange={(e) => handleSelectFilterChange(e)}
              id='exampleSelect'>
              <option value=''>All</option>
              {STATES.map((data, index) => {
                return (
                  <option value={data} key={index}>
                    {data}
                  </option>
                );
              })}
            </Input>
          </InputGroup>
        </div>
      </div>
      <Table bordered {...getTableProps}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                  {column.render('Header')}
                  <span className='ml-5'>
                    {column.isSorted ? (
                      column.isSortedDesc ? (
                        <FontAwesomeIcon icon={faLongArrowAltDown} />
                      ) : (
                        <FontAwesomeIcon icon={faLongArrowAltUp} />
                      )
                    ) : (
                      ''
                    )}
                  </span>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {page.map((row, i) => {
            prepareRow(row);
            return (
              <tr key={i} {...row.getRowProps()}>
                {row.cells.map((cell) => {
                  return (
                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </Table>
      <div className='d-flex mb-3 justify-content-end align-items-center'>
        <span>
          Page{' '}
          <strong>
            {pageIndex + 1} of {pageOptions.length}
          </strong>{' '}
        </span>
        <span className=' d-flex align-items-center'>
          | Go to page:{' '}
          <Input
            className='pageInput mx-1'
            type='number'
            defaultValue={pageIndex + 1}
            onChange={(e) => {
              const pageNumber = e.target.value
                ? Number(e.target.value) - 1
                : 0;
              gotoPage(pageNumber);
            }}></Input>
        </span>
        <Input
          className='w-auto'
          type='select'
          value={pageSize}
          onChange={(e) => {
            setPageSize(Number(e.target.value));
          }}>
          {[10, 25, 50].map((pageSize, index) => (
            <option key={index} value={pageSize}>
              Show {pageSize}
            </option>
          ))}
        </Input>
        <Button
          className='p-1 mx-1'
          onClick={() => {
            gotoPage(0);
          }}
          disabled={!canPreviousPage}>
          {'<<'}
        </Button>

        <Button
          className='p-1 mx-1'
          onClick={() => {
            previousPage();
          }}
          disabled={!canPreviousPage}>
          Previous
        </Button>
        <Button
          className='p-1 mx-1'
          onClick={() => {
            nextPage();
          }}
          disabled={!canNextPage}>
          Next
        </Button>
        <Button
          className='p-1 mx-1'
          onClick={() => {
            gotoPage(pageCount - 1);
          }}
          disabled={!canNextPage}>
          {'>>'}
        </Button>
      </div>
    </Container>
  );
};
