import { faPen, faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import axios from 'axios';
import { FastField, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import {
  Button,
  Form,
  FormFeedback,
  FormGroup,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Label,
  Modal,
  ModalBody,
  ModalHeader,
} from 'reactstrap';
import * as yup from 'yup';
import './Hml.css';

const Hml = ({ type, margin, hirarchy, selectedRegion, id }) => {
  const [createModel, setCreateModel] = useState(false);
  const toggle = () => {
    setCreateModel(!toggle);
  };
  const initialValues = {
    High: margin,
    Mid: margin,
    Low: margin,
  };
  let validationSchema = yup.object().shape({
    High: yup.number().required().max(100).positive(),
    Mid: yup.number().required().max(100).positive(),
    Low: yup.number().required().max(100).positive(),
  });
  const closeIcon = (
    <FontAwesomeIcon
      onClick={toggle}
      className='model_close_btn'
      icon={faTimes}
    />
  );
  const hhh = (e) => {
    console.log(e);
  };
  const onSubmit = async (props) => {
    console.log('props', props);
    axios
      .put(`http://localhost:3003/dealerMargin/${id}`, {
        hirarchy: hirarchy,
        state: selectedRegion,
        Low: props.Low,
        High: props.High,
        Mid: props.Mid,
      })
      .then((response) => {
        response.status === 200 ? hhh(response.data) : setCreateModel(true);
        console.log('Status: ', response.status);
        console.log('Data: ', response.data);
      })
      .catch((error) => {
        console.error('Something went wrong!', error);
      });
  };
  return (
    <div>
      <div className='add_margin my-3 d-flex'>
        <div className='d-flex icon_container'>
          <FontAwesomeIcon
            onClick={() => setCreateModel(!createModel)}
            className='editicon'
            icon={faPen}
          />
        </div>
        <p className='p'>Type</p>
        <h3>{type}</h3>
        <div className='margin'>
          <h1>{margin}%</h1>
          <p className='p'>Margin</p>
        </div>
        <div>
          <p className='p'>Hirarchy</p>
          <h3>{hirarchy}</h3>
        </div>
      </div>
      <Modal isOpen={createModel} toggle={toggle}>
        <ModalHeader toggle={toggle} close={closeIcon}></ModalHeader>
        <ModalBody>
          <div className='d-flex justify-content-center w-100 mb-2'>
            <h4>Edit Margin</h4>
          </div>
          <div className='defaults my-4'>
            <div className='d-flex justify-content-center'>
              <p className='region'>{'State: '} </p>
              <p className=''>{selectedRegion}</p>
            </div>
            <div className='d-flex justify-content-center'>
              <p className='region'>{'Hierarchy: '} </p>
              <p className=''>{hirarchy}</p>
            </div>
          </div>
          <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={(props) => {
              onSubmit(props);
            }}
            isInitialValid={() => validationSchema.isValidSync(initialValues)}>
            {({
              values,
              isValid,
              handleChange,
              handleSubmit,
              handleBlur,
              dirty,
            }) => (
              <>
                <Form className='form' onSubmit={handleSubmit}>
                  <FormGroup>
                    <Label for='Distributor' className='region'>
                      {type}
                    </Label>
                    <InputGroup>
                      <FastField
                        as={Input}
                        name={type}
                        value={
                          type === 'Low'
                            ? values.Low
                            : type === 'High'
                            ? values.High
                            : values.Mid
                        }
                        onChange={handleChange}
                        invalid={!isValid}
                        onBlur={handleBlur}
                        valid={isValid}
                      />
                      <InputGroupAddon addonType='append'>
                        <InputGroupText className='suffixbtn'>%</InputGroupText>
                      </InputGroupAddon>
                      <FormFeedback>Enter a valid number</FormFeedback>
                    </InputGroup>
                  </FormGroup>
                  <Button
                    onClick={(e) => hhh(e)}
                    type='submit'
                    className='w-100 mt-3'
                    color='secondary'
                    disabled={!dirty || !isValid}>
                    Save
                  </Button>
                </Form>
              </>
            )}
          </Formik>
        </ModalBody>
      </Modal>
    </div>
  );
};

export default Hml;
