import axios from 'axios';
import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from 'react-router';
import { Card, CardBody, Container, Label } from 'reactstrap';
import './Profile.css';
const Profile = () => {
  const [responseData, setResponseData] = useState('');
  let { id } = useParams();
  const loadProfile = useCallback(async () => {
    await axios
      .get(`http://localhost:3003/dealers?id=${id}`)
      .then((response) => {
        console.log('response', response);
        setResponseData(response.data[0]);
        console.log('responseData function', responseData);
      })
      .catch((error) => {
        console.log('error', error);
      });
  }, [id]);
  useEffect(() => {
    loadProfile();
  }, [loadProfile]);
  console.log('responseData', responseData);
  const personalDetails = [
    { title: 'Dealer Name', value: responseData.name },
    { title: 'Dealer Number', value: responseData.contact },
    { title: 'Registrations', value: responseData.registrations },
    { title: 'Billing Address', value: responseData.address },
  ];
  const bankDetails = [
    { title: 'Account Number', value: responseData.accountNumber },
    { title: 'UPI', value: responseData.upi },
    { title: 'Bank Name', value: responseData.bankName },
    { title: 'Card Number', value: responseData.cardNumber },
  ];
  const storeDetails = [
    { title: 'Shop Area', value: responseData.shopArea },
    { title: 'Shop Nature', value: responseData.shopNature },
    { title: 'Godown Area', value: responseData.godownArea },
  ];
  const contactDetils = [
    { title: 'Owner Name', value: responseData.ownerName },
    { title: 'Dealer Phone', value: responseData.dealerPhone },
    { title: 'My Manager', value: responseData.myManager },
    { title: 'Dealer Mail', value: responseData.dealerMail },
    { title: 'Promoter', value: responseData.promoter },
  ];
  const targetDetails = [
    {
      title: 'SKD Amount',
      to: responseData.skdAmount.to,
      from: responseData.skdAmount.from,
    },
    {
      title: 'Annual Amount',
      to: responseData.annualAmount.to,
      from: responseData.annualAmount.from,
    },
    {
      title: 'Quaterly Amount',
      to: responseData.quaterlyAmount.to,
      from: responseData.quaterlyAmount.from,
    },
  ];

  return (
    <Container>
      <h4 className='pt-4 pb-5'>Dealer's Profile</h4>
      {responseData === '' ? (
        ''
      ) : (
        <PersonalDetails heading='Personal Details' details={personalDetails} />
      )}

      <Details heading='Bank Details' details={bankDetails} />
      <Details heading='Bank Details' details={bankDetails} />
      <Details heading='Store Details' details={storeDetails} />
      <Details heading='Contact Details' details={contactDetils} />
      <TargetDetails heading='Target Details' details={targetDetails} />
      <Details heading='Bank Details' details={bankDetails} />
      <Details heading='Bank Details' details={bankDetails} />
    </Container>
  );
};

export default Profile;
const PersonalDetails = ({ heading, details }) => {
  console.log('details', details);
  return (
    <>
      <Card>
        <CardBody>
          <h4>{heading}</h4>
          <div className='d-flex'>
            <Label className=''>{details[0].title}</Label>:
            <p className='ml-2'> {details[0].value}</p>
          </div>
          <div className='d-flex'>
            <Label>{details[1].title}</Label>:
            <p className='ml-2'> {details[1].value}</p>
          </div>
          <div className='d-flex'>
            <Label>Registrations</Label>:
            <p
              className={
                details[2].value.gst ? 'validGst mx-2' : ' inValidGst mx-2'
              }>
              GST
            </p>
            <p
              className={
                details[2].value.pan ? 'validGst mx-2' : ' inValidGst mx-2'
              }>
              PAN
            </p>
            <p
              className={
                details[2].value.eCart ? 'validGst mx-2' : ' inValidGst mx-2'
              }>
              E-Cart
            </p>
          </div>
          <div className='d-flex'>
            <Label>{details[3].title}</Label>:
            <p className='ml-2'> {details[3].value}</p>
          </div>
        </CardBody>
      </Card>
    </>
  );
};
const Details = ({ heading, details }) => {
  return (
    <>
      <Card>
        {console.log('details card', details)}
        <CardBody>
          <h4>{heading}</h4>
          {details.map((data, index) => {
            return (
              <div className='d-flex' key={index}>
                <p>{data.title}</p>:<p className='ml-2'>{data.value}</p>
              </div>
            );
          })}
        </CardBody>
      </Card>
    </>
  );
};
const TargetDetails = ({ heading, details }) => {
  return (
    <>
      <Card>
        {console.log('details card', details)}
        <CardBody>
          <h4>{heading}</h4>
          {details.map((data, index) => {
            return (
              <div className='d-flex' key={index}>
                <p>{data.title}</p>:<p className='ml-2'>{data.to}/</p>/
                <p className='ml-2'>{data.from}/</p>
              </div>
            );
          })}
        </CardBody>
      </Card>
    </>
  );
};
