import { faPen, faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { FastField, Formik } from 'formik';
import React, { useState } from 'react';
import {
  Button,
  Form,
  FormFeedback,
  FormGroup,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Label,
  Modal,
  ModalBody,
  ModalHeader,
} from 'reactstrap';
import * as yup from 'yup';
import { boolean } from 'yup/lib/locale';
import './Hml.css';

const Hml = ({ type, margin, hirarchy, selectedRegion }) => {
  const [createModel, setCreateModel] = useState(false);
  const toggle = () => {
    setCreateModel(!toggle);
  };
  const initialValues = {
    High: margin,
    Mid: margin,
    Low: margin,
  };
  let validationSchema = yup.object().shape({
    High: yup.number().required().max(100).positive(),
    Mid: yup.number().required().max(100).positive(),
    Low: yup.number().required().max(100).positive(),
  });
  const closeIcon = (
    <FontAwesomeIcon
      onClick={toggle}
      className='model_close_btn'
      icon={faTimes}
    />
  );
  const onSubmit = (e, props) => {
    e.preventDefault();
    console.log(props);
  };
  return (
    <div>
      <div className='add_margin my-3 d-flex'>
        <div className='d-flex icon_container'>
          <FontAwesomeIcon
            onClick={() => setCreateModel(!createModel)}
            className='editicon'
            icon={faPen}
          />
        </div>
        <p className='p'>Type</p>
        <h3>{type}</h3>
        <div className='margin'>
          <h1>{margin}%</h1>
          <p className='p'>Margin</p>
        </div>
        <div>
          <p className='p'>Hirarchy</p>
          <h3>{hirarchy}</h3>
        </div>
      </div>
      <Modal isOpen={createModel} toggle={toggle}>
        <ModalHeader toggle={toggle} close={closeIcon}></ModalHeader>
        <ModalBody>
          <div className='d-flex justify-content-center w-100 mb-2'>
            <h4>Edit Margin</h4>
          </div>
          <div className='defaults my-4'>
            <div className='d-flex justify-content-center'>
              <p className='region'>{'State: '} </p>
              <p className=''>{selectedRegion}</p>
            </div>
            <div className='d-flex justify-content-center'>
              <p className='region'>{'Hierarchy: '} </p>
              <p className=''>{hirarchy}</p>
            </div>
          </div>
          <Formik
            initialValues={initialValues}
            validationSchema={validationSchema}
            onSubmit={(props) => onSubmit(props)}
            isInitialValid={() => validationSchema.isValidSync(initialValues)}>
            {({
              values,
              touched,
              errors,
              isValid,
              handleChange,
              handleBlur,
              setFieldValue,
              dirty,
            }) => (
              <>
                {console.log('errors', errors)}
                {console.log('dirty', dirty)}
                {console.log('touched', touched)}
                {console.log('Type', type)}
                {console.log('values', values)}
                {console.log('isvalid', isValid)}
                <Form className='form'>
                  <FormGroup>
                    <Label for='Distributor' className='region'>
                      {type}
                    </Label>
                    <InputGroup>
                      <FastField
                        as={Input}
                        name={type}
                        value={
                          type === 'Low'
                            ? values.Low
                            : type === 'High'
                            ? values.High
                            : values.Mid
                        }
                        onChange={handleChange}
                        invalid={
                          dirty && type === 'Low'
                            ? touched.Low
                            : type === 'Mid'
                            ? touched.Mid
                            : touched.High && type === 'Low'
                            ? errors.Low
                            : type === 'Mid'
                            ? errors.Mid
                            : errors.High
                        }
                        valid={
                          !dirty && type === 'Low'
                            ? !errors.Low
                            : type === 'Mid'
                            ? !errors.Mid
                            : !errors.High
                        }
                      />
                      <InputGroupAddon addonType='append'>
                        <InputGroupText className='suffixbtn'>%</InputGroupText>
                      </InputGroupAddon>
                      <FormFeedback>{`Enter a valid number + ${vaid}`}</FormFeedback>
                    </InputGroup>
                  </FormGroup>
                  <Button
                    // type='submit'
                    // onClick={(e, props) => {
                    //   onSubmit(e, props);
                    // }}
                    className='w-100 mt-3'
                    color='secondary'
                    disabled={!dirty || !isValid}>
                    Save
                  </Button>
                </Form>
              </>
            )}
          </Formik>
        </ModalBody>
      </Modal>
    </div>
  );
};

export default Hml;
