import axios from 'axios';
import React, { useCallback, useEffect, useState } from 'react';
import { Container, Input } from 'reactstrap';
import { faPen, faTimes } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { ErrorMessage, FastField, Field, Formik } from 'formik';
import {
  Button,
  Form,
  FormFeedback,
  FormGroup,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Label,
  Modal,
  ModalBody,
  ModalHeader,
} from 'reactstrap';
import * as yup from 'yup';
import './DealerMargin.css';
const DealerMargin = () => {
  //=======================================================states
  const [region, setRegion] = useState('');
  const [allStates, setAllStates] = useState('');
  const [dataResponse, setDataResponse] = useState('');
  const [createModelHigh, setCreateModelHigh] = useState(false);
  const [createModelLow, setCreateModelLow] = useState(false);
  const [createModelMid, setCreateModelMid] = useState(false);
  const [addMarig, setAddMarig] = useState(false);
  const [initialValues, setInitialValues] = useState('');

  const toggleHigh = () => {
    setCreateModelHigh(!createModelHigh);
  };
  const toggleLow = () => {
    setCreateModelLow(!createModelLow);
  };
  const toggleMid = () => {
    setCreateModelMid(!createModelMid);
  };
  const toggle = (e) => {
    console.log(e);
    setAddMarig(!addMarig);
  };

  //========================================initialValues
  const addInitials = {
    High: '',
    Mid: '',
    Low: '',
    hierarchy: '',
  };

  const onSubmitAdd = async (props) => {
    console.log(props);
  };
  const onSubmit = async (props) => {
    axios
      .put(`http://localhost:3003/dealerMargin/${dataResponse[0].id}`, {
        hierarchy: dataResponse[0].hierarchy,
        state: dataResponse[0].state,
        Low: props.Low,
        High: props.High,
        Mid: props.Mid,
      })
      .then((response) => {
        setInitialValues(response.data);
        setCreateModelHigh(false);
        setCreateModelLow(false);
        setCreateModelMid(false);
        LoadMargin();
      })
      .catch((error) => {
        console.error('Something went wrong!', error);
      });
  };
  //===========================================selectHandle
  const selectHandle = (e) => {
    setRegion(e.target.value);
  };

  //=================================loadMargin
  const LoadMargin = useCallback(async () => {
    const result = await axios.get('http://localhost:3003/dealerMargin');
    const state = [];
    const stat = [];
    for (let index = 0; index < result.data.length; index++) {
      const element = result.data[index];
      element.state === region ? stat.push(element) : console.log('hello');
      element.state === region
        ? setInitialValues(element)
        : console.log('hello');
      state.push(result.data[index].state);
      console.log('element', element);
    }
    setAllStates(state);
    setDataResponse(stat);
  }, [region]);
  //======================================================useEffect
  useEffect(() => {
    LoadMargin();
  }, [LoadMargin]);
  //===========================================validationSchema
  let validationSchema = yup.object().shape({
    High: yup.number().required().max(100).positive(),
    Mid: yup.number().required().max(100).positive(),
    Low: yup.number().required().max(100).positive(),
  });
  let addMarginSchema = yup.object().shape({
    High: yup.number().required().max(100).positive(),
    Mid: yup.number().required().max(100).positive(),
    Low: yup.number().required().max(100).positive(),
    hierarchy: yup.string().required(),
  });

  return (
    //=====================================================container
    <Container>
      <h4 className='pt-4 pb-5'>Dealer Margin</h4>
      {/* ============================================================select */}
      <div className='selectState'>
        <label className='label' htmlFor='State'>
          State
        </label>

        <Input type='select' defaultValue='Set state' onChange={selectHandle}>
          <option value=''>Select state</option>
          {allStates === ''
            ? ''
            : allStates.map((data, index) => {
                return (
                  <option key={index} value={data} name={data}>
                    {data}
                  </option>
                );
              })}
        </Input>
      </div>
      {/* =================================================condition  */}
      {dataResponse.length === 0 ? (
        console.log('challo')
      ) : dataResponse[0].hierarchy === '' ? (
        // ===========================================================add Margin
        <div
          onClick={(e) => {
            toggle(e);
          }}
          className='d-flex justify-content-lg-between mt-3'>
          <div className='add_margins d-flex'>+ Add Margin</div>
          <Formik
            initialValues={addInitials}
            validationSchema={addMarginSchema}
            onSubmit={(props) => {
              onSubmitAdd(props);
            }}
            isInitialValid={() => validationSchema.isValidSync(addInitials)}>
            {({
              values,
              isValid,
              handleChange,
              handleSubmit,
              handleBlur,
              errors,
              dirty,
            }) => (
              <>
                <Modal isOpen={addMarig} toggle={toggle}>
                  <ModalHeader
                    toggle={toggleHigh}
                    close={
                      <FontAwesomeIcon
                        onClick={toggle}
                        className='model_close_btn'
                        icon={faTimes}
                      />
                    }></ModalHeader>
                  <ModalBody>
                    <div className='d-flex justify-content-center w-100 mb-2'>
                      <h4>Add Margin</h4>
                    </div>
                    <div className='d-flex justify-content-center defaults my-4'>
                      <p className='region'>{'State: '} </p>
                      <p className=''>{region}</p>
                    </div>
                    <Form className='form' onSubmit={handleSubmit}>
                      <FormGroup>
                        <Label>Hierarchy</Label>
                        <Field
                          name='hierarchy'
                          render={({ field }) => (
                            <Input
                              {...field}
                              invalid={!isValid}
                              onBlur={handleBlur}
                              valid={isValid}
                              invalid={!isValid}
                              type='select'
                              value={values.hierarchy}
                              onChange={handleChange}
                              // (e) => console.log('handleChange', e)}
                            >
                              <option value='hello'>Hello</option>
                              <option value='hello1'>Hello1</option>
                              <option value='hello2'>Hello2</option>
                            </Input>
                          )}
                        />
                        {console.log('errors.hierarchy', errors.hierarchy)}
                        {console.log('values.hierarchy', values.hierarchy)}
                        {/* <Input
                          // tag={Field}
                          type='select'
                          name='hierarchy'
                          onChange={(e) => {
                            console.log('dropdown', e);
                          }}
                          invalid={!isValid}
                          onBlur={handleBlur}
                          valid={isValid}>
                          <option>Hello</option>
                          <option>Hello1</option>
                          <option>Hello2</option>
                        </Input> */}
                        <FormFeedback>{ErrorMessage}</FormFeedback>
                      </FormGroup>
                      <FormGroup>
                        <Label for='Distributor' className='region'>
                          High
                        </Label>
                        <InputGroup>
                          <FastField
                            as={Input}
                            name='High'
                            value={values.High}
                            onChange={handleChange}
                            invalid={!isValid}
                            onBlur={handleBlur}
                            valid={isValid}
                          />
                          <InputGroupAddon addonType='append'>
                            <InputGroupText className='suffixbtn'>
                              %
                            </InputGroupText>
                          </InputGroupAddon>
                          <FormFeedback>Enter a valid number</FormFeedback>
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <Label for='Distributor' className='region'>
                          Mid
                        </Label>
                        <InputGroup>
                          <FastField
                            as={Input}
                            name='Mid'
                            value={values.Mid}
                            onChange={handleChange}
                            invalid={!isValid}
                            onBlur={handleBlur}
                            valid={isValid}
                          />
                          <InputGroupAddon addonType='append'>
                            <InputGroupText className='suffixbtn'>
                              %
                            </InputGroupText>
                          </InputGroupAddon>
                          <FormFeedback>Enter a valid number</FormFeedback>
                        </InputGroup>
