import { faEye, faTrash } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import axios from 'axios';
import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { useFilters, usePagination, useSortBy, useTable } from 'react-table';
import { Button } from 'reactstrap';
import TableTax from '../../../components/Table/TableTax';
const ITEMS_COLUMN = [
  {
    Header: 'SKU',
    accessor: 'SKU',
  },
  { Header: 'Product Hierarchy', accessor: 'Hierarchy' },
  { Header: 'Product Category', accessor: 'Category' },
  { Header: 'Product Type', accessor: 'Type' },
  { Header: 'HML Category', accessor: 'HMLCategory' },
  { Header: 'MRP', accessor: 'MRP' },
  {
    Header: 'Actions',
    accessor: 'action',
    disableSortBy: true,
    Cell: (props) => {
      // const rowIdx = props.row.original.id;
      return (
        <div className='d-flex justify-content-around'>
          <Button>Edit</Button>
        </div>
      );
    },
  },
];
const Item = () => {
  const [item, setItem] = useState('');
  const [states, setStates] = useState('');
  const Loaditem = useCallback(async () => {
    const result = await axios
      .get('http://localhost:3003/item')
      .then((response) => {
        setItem(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  });
  const editUser = useCallback(
    async (e, rowIdx) => {
      e.preventDefault();
      await axios
        .put(`http://localhost:3003/item/${rowIdx}`, {})
        .then((response) => {
          console.log('response', response);
        })
        .catch((error) => {
          console.log('error', error);
        });
      Loaditem();
    },
    [Loaditem]
  );
  useEffect(() => {
    Loaditem();
  }, [Loaditem]);

  console.log('columns', columns);
  console.log('columns', columns);
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    page,
    nextPage,
    previousPage,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state,
    gotoPage,
    pageCount,
    setPageSize,
    prepareRow,
    setFilter,
  } = useTable(
    {
      ITEMS_COLUMN,
      item,
    },
    useFilters,
    useSortBy,
    usePagination
  );
  const { pageIndex, pageSize } = state;
  return item === '' ? (
    <div>{console.log('item in empty', item)}</div>
  ) : (
    <>
      {/* <TableTax
        columns={columns}
        data={data}
        getTableProps={getTableProps}
        getTableBodyProps={getTableBodyProps}
        headerGroups={headerGroups}
        page={page}
        nextPage={nextPage}
        previousPage={previousPage}
        canNextPage={canNextPage}
        canPreviousPage={canPreviousPage}
        pageOptions={pageOptions}
        state={state}
        gotoPage={gotoPage}
        pageCount={pageCount}
        setPageSize={setPageSize}
        prepareRow={prepareRow}
        setFilter={setFilter}
        pageIndex={pageIndex}
        pageSize={pageSize}
      /> */}
    </>
  );
};

export default Item;
