import React from 'react';
import { useParams } from 'react-router';
import { Button, Col, Container, Row, Table } from 'reactstrap';
import { ORDER } from '../../assets/Json/ORDER';
import './OrderDetails.css';
const OrderDetails = (props) => {
  let param = useParams();
  const i = parseInt(param.id);
  const data = ORDER[i];
  console.log('data', data);
  console.log('orders');

  return (
    <Container>
      <h4 className='pt-4 pb-4'>Order Details</h4>
      <div className='cardDetials'>
        <h4 className='text-light p-3 distributorHeading'>
          {data.distributor}
        </h4>
        <Row className='px-3'>
          <Col
            xs={3}
            sm={3}
            md={3}
            lg={2}
            xl={2}
            className='pb-1 text-light column1'>
            Order ID
          </Col>
          <Col
            xs={9}
            sm={9}
            md={9}
            lg={6}
            xl={6}
            className='pb-1 text-light column2'>
            : {data.id}
          </Col>
          <Col xs={3} sm={3} md={3} lg={2} xl={2} className='pb-1 text-light column1'>
            Total Item
          </Col>
          <Col xs={9} sm={9} md={9} lg={2} xl={2} className='pb-1 text-light column2'>
            : {data.totalItem}
          </Col>
        </Row>
        <Row className='px-3'>
          <Col xs={3} sm={3} md={3} lg={2} xl={2} className='pb-1 text-light column1'>
            Order Date
          </Col>
          <Col xs={9} sm={9} md={9} lg={6} xl={6} className='pb-1 text-light'>
            : {data.date}
          </Col>
          <Col xs={3} sm={3} md={3} lg={2} xl={2} className='pb-1 text-light column1'>
            Amount
          </Col>
          <Col xs={9} sm={9} md={9} lg={2} xl={2} className='pb-1 text-light column2'>
            : {data.amount}
          </Col>
        </Row>
        <Row className='px-3 pb-4'>
          <Col xs={3} sm={3} md={3} lg={2} xl={2} className='pb-1 text-light column1'>
            Address
          </Col>
          <Col xs={9} sm={9} md={9} lg={6} xl={6} className='pb-1 text-light'>
            : {data.address}
          </Col>
          <Col xs={3} sm={3} md={3} lg={2} xl={2} className='pb-1 text-light column1'>
            Status
          </Col>
          <Col xs={9} sm={9} md={9} lg={2} xl={2} className='pb-1 text-light column2'>
            : {data.status}
          </Col>
        </Row>
      </div>
      <div className='tableDiv mt-5 mb-3'>
        <Table>
          <thead>
            <tr>
              <th>Item</th>
              <th>Size</th>
              <th>Quantity</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {data.orders.map((data, i) => {
              return (
                <tr key={i}>
                  <td>{data.item}</td>
                  <td>{data.size}</td>
                  <td>{data.quantity}</td>
                  <td>{data.price}</td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </div>
      <div className='d-flex w-100 justify-content-end'>
        <Button color='danger' className='mr-3 mb-3'>
          Cancel
        </Button>
        <Button className='mb-3'>Order Dispatch</Button>
      </div>
    </Container>
  );
};

export default OrderDetails;
