import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { useMemo, useState } from 'react';
import { useFilters, usePagination, useSortBy, useTable } from 'react-table';
import {
  Button,
  Container,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
} from 'reactstrap';
import { TAX_TRANSITION_VALUE, TAX_PER_UNIT } from '../../assets/Json/TAX';
import TableTax from '../../components/Table/TableTax';
export const TAX_PER_UNIT_COLUMN = [
  {
    Header: 'HSN Code',
    accessor: 'code',
  },
  {
    Header: 'Range From',
    accessor: 'rangeFrom',
  },
  {
    Header: 'Range To',
    accessor: 'rangeTo',
  },
  {
    Header: 'GST',
    accessor: 'gst',
  },

  {
    Header: 'Valid From',
    accessor: 'validFrom',
  },

  { Header: 'ValidTill', accessor: 'validTill' },

  {
    Header: 'Actions',
    accessor: 'action',
    disableSortBy: true,
    Cell: () => {
      return (
        <>
          <span
            onClick={() => {
              return null;
            }}>
            <Button className='button delete'>Delete</Button>
          </span>

          <span
            onClick={() => {
              return null;
            }}>
            <Button className='button edit ml-4'>Edit</Button>
          </span>
        </>
      );
    },
  },
];

const TAX_TRANSITION_VALUE_COLUMN = [
  {
    Header: 'HSN Code',
    accessor: 'code',
  },

  {
    Header: 'GST',
    accessor: 'gst',
  },

  {
    Header: 'From',
    accessor: 'from',
  },

  { Header: 'To', accessor: 'to' },

  {
    Header: 'Actions',
    accessor: 'action',
    disableSortBy: true,
    Cell: () => {
      return (
        <>
          <span
            onClick={() => {
              return null;
            }}>
            <Button className='button edit'>Delete</Button>
          </span>

          <span
            onClick={() => {
              return null;
            }}>
            <Button className='button delete ml-4'>Edit</Button>
          </span>
        </>
      );
    },
  },
];
const Tax = () => {
  const [transition, settransition] = useState(false);
  const memoizedTTVC = useMemo(() => TAX_TRANSITION_VALUE_COLUMN, []);
  const memoizedTTV = useMemo(() => TAX_TRANSITION_VALUE, []);
  const memoizedTPUC = useMemo(() => TAX_PER_UNIT_COLUMN, []);
  const memoizedTPU = useMemo(() => TAX_PER_UNIT, []);
  const columns = transition ? memoizedTPUC : memoizedTTVC;
  const data = transition ? memoizedTPU : memoizedTTV;
  const [filterInput, setFilterInput] = useState('');
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    page,
    nextPage,
    previousPage,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state,
    gotoPage,
    pageCount,
    setPageSize,
    prepareRow,
    setFilter,
  } = useTable(
    {
      columns,
      data,
    },
    useFilters,
    useSortBy,
    usePagination
  );
  const { pageIndex, pageSize } = state;
  const handleSelectFilterChange = (e) => {
    const value = e.target.value === 'true' ? true : false;
    settransition(value);
    console.log(typeof value, value);
  };
  const handleFilterChange = (e) => {
    const value = e.target.value || undefined;

    setFilter('code', value);
    setFilterInput(value);
  };
  return (
    <Container>
      <h4 className='pt-4 pb-5'>Tax</h4>
      <div className='d-flex w-60 ml-3  mb-3 filters'>
        <div className='d-flex flex-column mr-3 w-100'>
          <span>HSN Code</span>
          <InputGroup size='sm'>
            <Input
              value={filterInput}
              onChange={handleFilterChange}
              placeholder='Search by HSN Code'
            />
            <InputGroupAddon addonType='append'>
              <InputGroupText>
                <FontAwesomeIcon className='icon' icon={faSearch} />
              </InputGroupText>
            </InputGroupAddon>
          </InputGroup>
        </div>
        <div className='d-flex flex-column mr-3 w-100'>
          <span>State</span>
          <InputGroup size='sm'>
            <Input type='select' onChange={(e) => handleSelectFilterChange(e)}>
              {[
                { name: 'Transition Value', value: false },
                { name: 'Per-Unit', value: true },
              ].map((data, index) => {
                return (
                  <option value={data.value} key={index}>
                    {data.name}
                  </option>
                );
              })}
            </Input>
          </InputGroup>
        </div>
      </div>

      <TableTax
        columns={columns}
        data={data}
        getTableProps={getTableProps}
        getTableBodyProps={getTableBodyProps}
        headerGroups={headerGroups}
        page={page}
        nextPage={nextPage}
        previousPage={previousPage}
        canNextPage={canNextPage}
        canPreviousPage={canPreviousPage}
        pageOptions={pageOptions}
        state={state}
        gotoPage={gotoPage}
        pageCount={pageCount}
        setPageSize={setPageSize}
        prepareRow={prepareRow}
        setFilter={setFilter}
        pageIndex={pageIndex}
        pageSize={pageSize}
      />
    </Container>
  );
};

export default Tax;
